import React from 'react';
import { Route } from 'react-router-dom';
import { connect } from 'react-redux';

import { fetchCollectionsStartAsync } from '../../redux/shop/shop.actions';

import { selectorIsCollectionFetching } from '../../redux/shop/shop.selectors';

import WithSpinner from '../../components/with-spinner/with-spinner.component';

import CollectionsOverview from '../../components/collections-overview/collections-overview.component';
import CollectionPage from '../collection/collection.component';

const CollectionsOverviewWithSpinner = WithSpinner(CollectionsOverview);
const CollectionPageWithSpinner = WithSpinner(CollectionPage);

class ShopPage extends React.Component {



  componentDidMount() {
   const {fetchCollectionsStartAsync} =this.props;
   fetchCollectionsStartAsync();
  }

  render() {
    const { match,isFetchingLoading } = this.props;

    return (
      <div className='shop-page'>
        <Route
          exact
          path={`${match.path}`}
          render={props => (
            <CollectionsOverviewWithSpinner isLoading={isFetchingLoading} {...props} />
          )}
        />
        <Route
          path={`${match.path}/:collectionId`}
          render={props => (
            <CollectionPageWithSpinner isLoading={isFetchingLoading} {...props} />
          )}
        />
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  isFetchingLoading : selectorIsCollectionFetching(state)
})

const mapDispatchToProps = dispatch => ({
  fetchCollectionsStartAsync : dispatch => dispatch(fetchCollectionsStartAsync())
});

export default connect(
  null,
  mapDispatchToProps
)(ShopPage);
