import SHOP_DATA from "./shop.data";
import ShopActionTypes from "./shop.type";
const INIT_STATE = {
    collections : null,
    isFetching : false,
    errorMessager:undefined
}

const ShopAction = (state = INIT_STATE , action) => {
    switch(action.type){
        case ShopActionTypes.FETCH_COLLECTIONS_START :
            return {
                ...state,
                isFetching:true
            }
        
        case ShopActionTypes.FETCH_COLLECTIONS_SUCCESS:
            return {
                ...state,
                isFetching:false,
                collections:action.payload
            }
        case ShopActionTypes.FETCH_COLLECTIONS_FAILUERE :
            return {
                ...state,
                isFetching:false,
                errorMessager : action.payload
            }
        default:
            return state;
    }
}

export default ShopAction;
