import React from 'react';
import CollectionOverView from '../../Component/collection-overview/collection-overview.component';
import { Route } from 'react-router-dom';
import Collections from '../collection/collection.component';
import {connect} from 'react-redux'
import { fetchCollectionsStartAsync,fetchCollectionsFailure,fetchCollectionsSuccess } from '../../redux/shop/shop.action';
import WithSpinner from '../../Component/with-spinner/with-spinner.component';
import { selectorColelctionFetching,selectortCollectionShop } from '../../redux/shop/shop.selector';



const ColelctionOverviewWithSpinner = WithSpinner(CollectionOverView);

const CollectionPageWithSpinner = WithSpinner(Collections);

class ShopPage extends React.Component{

   componentDidMount(){
      const {fetchCollectionsStartAsync}  = this.props;

      fetchCollectionsStartAsync();
   }

   render(){
      const {match,isCollectionFetching,isCollectionLoading} = this.props;
      return  (
       <div className='shop-page'>
          <Route exact path = {`${match.path}`} render={ (props) => <ColelctionOverviewWithSpinner isLoading={isCollectionFetching} {...props} />} />
           <Route path ={`${match.path}/:CollectionsId`} render={(props) =>  <CollectionPageWithSpinner isLoading={isCollectionLoading} {...props} />} />
       </div>
    )
   }
}

const mapStateToPRops = (state) => ({
   isCollectionFetching : selectorColelctionFetching(state),
   isCollectionLoading : selectortCollectionShop(state)
})

const mapDispatchToProps = dispatch => ({
  fetchCollectionsStartAsync: () => dispatch(fetchCollectionsStartAsync())
});
   
export default connect(mapStateToPRops,mapDispatchToProps)(ShopPage);
