import SHOP_DATA from "./shop.data";

import ShopActionTypes from "./shop.type";

export const updateColelction =(collectionMap) => ({
    type:ShopActionTypes.UPDATE_COLLECTION,
    payload:collectionMap
})

export const fetchCollectionsSuccess = collectionsMap => ({
    type:ShopActionTypes.FETCH_COLLECTIONS_SUCCESS,
    payload:collectionsMap
})

export const fetchCollectionsFailure = errorMessager => ({
    type:ShopActionTypes.FETCH_COLLECTIONS_FAILUERE,
    payload : errorMessager
})

const fetchCollectionsStart = () => ({
    type:ShopActionTypes.FETCH_COLLECTIONS_START
})

export const fetchCollectionsStartAsync = () => {
    return dispatch =>  {
        const {updateColelction} = this.props;
        const collectionRef = firestore.collection('collections');
         dispatch(fetchCollectionsStart());
        collectionRef.get().then(snapshot => {
           
            const collectionMap = convertCollectionSnapshotToMap(snapshot)
            // updateColelction(collectionMap);
            // this.setState({loading:false});
          }).catch(error => dispatch(fetchCollectionsFailure(error)));
    }

}
