import React from 'react';
import CollectionOverView from '../../Component/collection-overview/collection-overview.component';
import { Route } from 'react-router-dom';
import Collections from '../collection/collection.component';
import { firestore,convertCollectionSnapshotToMap } from '../../firebase/firebase.utils';
import {connect} from 'react-redux'
import { updateColelction } from '../../redux/shop/shop.action';
import WithSpinner from '../../Component/with-spinner/with-spinner.component';

const colelctionOverviewWithSpinner = WithSpinner(CollectionOverView);

const collectionPageWithSpinner = WithSpinner(Collections);

class ShopPage extends React.Component{

   state = {
      loadding : true
   }


   unSubscribeFromSnapshot = null;

   componentDidMount(){
      const {updateColelction} = this.props;
      const collectionRef = firestore.collection('collections');
      
      collectionRef.onSnapshot(async snapshot => {
        const collectionMap = convertCollectionSnapshotToMap(snapshot)
        updateColelction(collectionMap);
        this.setState({loading:false});
      })
   }

   render(){
      const {match} = this.props;
      return  (
       <div className='shop-page'>
          <Route exact path = {`${match.path}`} render={ (props) => colelctionOverviewWithSpinner} />
           <Route path ={`${match.path}/:CollectionsId`} component={collectionPageWithSpinner} />
       </div>
    )
   }
}

const mapDispatchToProps = dispatch => ({
   updateColelction : collectionMap => dispatch(updateColelction(collectionMap))
})

   
export default connect(null,mapDispatchToProps)(ShopPage);
